import React, {useEffect, useState} from 'react';
import {MarkItDown} from '../mark-it-down'
import {render} from "react-dom";
import {IonItem} from '@ionic/react';

type Props = { markdownFile: string };

export default function RenderMarkdown({markdownFile}: Props) {
    let mdContainer: any;
    useEffect(() => {
        mdContainer = document.getElementById('md-container');
    })
    const [renderedMd, updatedHttpCall] = useState('');
    useEffect(() => {
        fetch(`/${markdownFile}.md`).then(response => response.text().then(body => {
            updatedHttpCall(MarkItDown(body));
            mdContainer.innerHTML = renderedMd;
            const headingElement = document.querySelectorAll('h1');
            ionItemHeading(headingElement[0]);
            console.log(headingElement[0]);
        }))
    })

